// src/utils/markdownParser.js
// MarkdownËß£ÊûêÂºïÊìé - ‰∏ìÈó®Ëß£ÊûêAIÁîüÊàêÁöÑÈ¢òÁõÆÊñáÊ°£

import { MARKDOWN_PATTERNS, QUESTION_TYPES } from './constants.js';

/**
 * Ëß£ÊûêMarkdownÊñáÊ°£ÔºåÊèêÂèñÈ¢òÁõÆ‰ø°ÊÅØ
 * @param {string} markdownText - MarkdownÊñáÊ°£ÂÜÖÂÆπ
 * @param {string[]} baseTags - Âü∫Á°ÄÊ†áÁ≠æÔºàËØæÁ®ã‰ø°ÊÅØÔºâ
 * @returns {Object[]} - Ëß£ÊûêÂá∫ÁöÑÈ¢òÁõÆÊï∞ÁªÑ
 */
export const parseMarkdownQuestions = (markdownText, baseTags = []) => {
  console.log('üìñ ÂºÄÂßãËß£ÊûêMarkdownÊñáÊ°£');
  console.log('üìù ÊñáÊ°£ÈïøÂ∫¶:', markdownText.length);
  console.log('üè∑Ô∏è Âü∫Á°ÄÊ†áÁ≠æ:', baseTags);

  if (!markdownText || typeof markdownText !== 'string') {
    console.warn('‚ùå ËæìÂÖ•ÊñáÊ°£‰∏∫Á©∫ÊàñÊ†ºÂºèÈîôËØØ');
    return [];
  }

  const lines = markdownText.split('\n');
  const questions = [];

  let currentQuestion = null;
  let currentSection = 'question'; // question, solution, answer
  let lineNumber = 0;

  console.log(`üìÑ ÊÄªË°åÊï∞: ${lines.length}`);

  for (const line of lines) {
    lineNumber++;
    const trimmedLine = line.trim();

    // Ë∑≥ËøáÁ©∫Ë°å
    if (!trimmedLine) continue;

    // Ê£ÄÊü•ÊòØÂê¶ÊòØÊñ∞È¢òÁõÆÂºÄÂßã
    const questionMatch = matchQuestionPattern(trimmedLine);

    if (questionMatch) {
      // ‰øùÂ≠ò‰∏ä‰∏ÄÈÅìÈ¢òÁõÆ
      if (currentQuestion && isValidQuestion(currentQuestion)) {
        const processedQuestion = processQuestion(currentQuestion, baseTags);
        questions.push(processedQuestion);
        console.log(`‚úÖ ÂÆåÊàêÈ¢òÁõÆ: ${processedQuestion.questionType}`);
      }

      // ÂºÄÂßãÊñ∞È¢òÁõÆ
      console.log(`üÜï ÂèëÁé∞Êñ∞È¢òÁõÆ: ${questionMatch.type} (Ë°å ${lineNumber})`);
      currentQuestion = {
        questionType: questionMatch.type,
        questionText: questionMatch.content || '',
        answer: '',
        solutionSteps: '',
        rawLine: trimmedLine,
        startLine: lineNumber
      };
      currentSection = 'question';
      continue;
    }

    // Â¶ÇÊûúÊ≤°ÊúâÂΩìÂâçÈ¢òÁõÆÔºåË∑≥ËøáËøôË°å
    if (!currentQuestion) continue;

    // Ê£ÄÊü•Ëß£È¢òÊÄùË∑ØÊ†áËØÜ
    if (isSolutionSection(trimmedLine)) {
      console.log(`üí° ËøõÂÖ•Ëß£È¢òÊÄùË∑ØÈÉ®ÂàÜ (Ë°å ${lineNumber})`);
      currentSection = 'solution';
      continue;
    }

    // Ê£ÄÊü•Á≠îÊ°àÊ†áËØÜ
    const answerMatch = matchAnswerPattern(trimmedLine);
    if (answerMatch) {
      console.log(`‚úÖ ÊâæÂà∞Á≠îÊ°à (Ë°å ${lineNumber}): ${answerMatch.substring(0, 50)}...`);
      currentQuestion.answer = answerMatch;
      continue;
    }

    // Â§ÑÁêÜÊôÆÈÄöÂÜÖÂÆπË°å
    if (isContentLine(trimmedLine)) {
      processContentLine(currentQuestion, trimmedLine, currentSection);
    }
  }

  // Â§ÑÁêÜÊúÄÂêé‰∏ÄÈÅìÈ¢òÁõÆ
  if (currentQuestion && isValidQuestion(currentQuestion)) {
    const processedQuestion = processQuestion(currentQuestion, baseTags);
    questions.push(processedQuestion);
    console.log(`‚úÖ ÂÆåÊàêÊúÄÂêé‰∏ÄÈ¢ò: ${processedQuestion.questionType}`);
  }

  console.log(`üéâ Ëß£ÊûêÂÆåÊàê! ÂÖ±Ëß£ÊûêÂá∫ ${questions.length} ÈÅìÈ¢òÁõÆ`);
  return questions;
};

/**
 * ÂåπÈÖçÈ¢òÁõÆÊ®°Âºè
 */
const matchQuestionPattern = (line) => {
  for (const pattern of MARKDOWN_PATTERNS.questionPatterns) {
    const match = line.match(pattern);
    if (match) {
      return {
        type: match[1],           // ‰æã1, ‰π†È¢ò1Á≠â
        content: match[2] || '',  // È¢òÁõÆÂÜÖÂÆπ
        fullMatch: match[0]
      };
    }
  }
  return null;
};

/**
 * ÂåπÈÖçÁ≠îÊ°àÊ®°Âºè
 */
const matchAnswerPattern = (line) => {
  for (const pattern of MARKDOWN_PATTERNS.answerPatterns) {
    const match = line.match(pattern);
    if (match) {
      return match[1].replace(/\*+/g, '').trim();
    }
  }
  return null;
};

/**
 * Ê£ÄÊü•ÊòØÂê¶ÊòØËß£È¢òÊÄùË∑ØÈÉ®ÂàÜ
 */
const isSolutionSection = (line) => {
  return MARKDOWN_PATTERNS.solutionKeywords.some(keyword => 
    line.includes(keyword)
  );
};

/**
 * Ê£ÄÊü•ÊòØÂê¶ÊòØÊúâÊïàÁöÑÂÜÖÂÆπË°å
 */
const isContentLine = (line) => {
  // ÊéíÈô§MarkdownÊ†áËÆ∞Ë°å
  const excludePatterns = [
    /^#+\s/,           // Ê†áÈ¢òË°å
    /^-{3,}$/,         // ÂàÜÈöîÁ∫ø
    /^\*{3,}$/,        // ÂàÜÈöîÁ∫ø
    /^={3,}$/,         // ÂàÜÈöîÁ∫ø
    /^\s*$/            // Á©∫Ë°å
  ];

  return !excludePatterns.some(pattern => pattern.test(line)) && 
         line.length > 2;
};

/**
 * Â§ÑÁêÜÂÜÖÂÆπË°å
 */
const processContentLine = (question, line, section) => {
  if (section === 'solution') {
    question.solutionSteps += line + '\n';
  } else if (section === 'question') {
    // Â¶ÇÊûúÈ¢òÁõÆÊñáÂ≠ó‰∏∫Á©∫ÔºåËøôÊòØÁ¨¨‰∏ÄË°å
    if (!question.questionText || question.questionText.trim() === '') {
      question.questionText = line;
    } else {
      // Âê¶ÂàôËøΩÂä†Âà∞È¢òÁõÆÂÜÖÂÆπÔºàÁî®Á©∫Ê†ºËøûÊé•Ôºâ
      question.questionText += ' ' + line;
    }
  }
};

/**
 * È™åËØÅÈ¢òÁõÆÊòØÂê¶ÊúâÊïà
 */
const isValidQuestion = (question) => {
  const hasValidType = QUESTION_TYPES.includes(question.questionType);
  const hasContent = question.questionText && question.questionText.trim().length > 5;

  if (!hasValidType) {
    console.warn(`‚ö†Ô∏è Êó†ÊïàÁöÑÈ¢òÁõÆÁ±ªÂûã: ${question.questionType}`);
  }

  if (!hasContent) {
    console.warn(`‚ö†Ô∏è È¢òÁõÆÂÜÖÂÆπËøáÁü≠Êàñ‰∏∫Á©∫: ${question.questionText}`);
  }

  return hasValidType && hasContent;
};

/**
 * Â§ÑÁêÜÈ¢òÁõÆÔºåÊ∑ªÂä†Âü∫Á°Ä‰ø°ÊÅØ
 */
const processQuestion = (rawQuestion, baseTags) => {
  return {
    questionType: rawQuestion.questionType,
    questionText: cleanText(rawQuestion.questionText),
    answer: cleanText(rawQuestion.answer),
    solutionSteps: cleanText(rawQuestion.solutionSteps),
    tags: [...baseTags], // ‰ΩøÁî®‰º†ÂÖ•ÁöÑÂü∫Á°ÄÊ†áÁ≠æ
    createdAt: new Date().toISOString(),
    metadata: {
      parsedAt: new Date().toISOString(),
      startLine: rawQuestion.startLine,
      originalLength: rawQuestion.questionText.length
    }
  };
};

/**
 * Ê∏ÖÁêÜÊñáÊú¨ÂÜÖÂÆπ
 */
const cleanText = (text) => {
  if (!text || typeof text !== 'string') return '';

  return text
    .trim()                          // ÂéªÈô§È¶ñÂ∞æÁ©∫Ê†º
    .replace(/\*+/g, '')            // ÂéªÈô§MarkdownÁ≤ó‰ΩìÊ†áËÆ∞
    .replace(/_{2,}/g, '')          // ÂéªÈô§‰∏ãÂàíÁ∫ø
    .replace(/\s+/g, ' ')           // ÂêàÂπ∂Â§ö‰∏™Á©∫Ê†º
    .replace(/\n\s*\n/g, '\n')      // ÂêàÂπ∂Â§ö‰∏™Êç¢Ë°å
    .trim();
};

/**
 * È™åËØÅËß£ÊûêÁªìÊûú
 */
export const validateParseResult = (questions) => {
  const validation = {
    isValid: true,
    totalQuestions: questions.length,
    issues: [],
    statistics: {
      byType: {},
      averageLength: 0,
      withAnswers: 0,
      withSolutions: 0
    }
  };

  let totalLength = 0;

  questions.forEach((question, index) => {
    // ÁªüËÆ°È¢òÁõÆÁ±ªÂûã
    validation.statistics.byType[question.questionType] = 
      (validation.statistics.byType[question.questionType] || 0) + 1;

    // ÁªüËÆ°ÈïøÂ∫¶
    totalLength += question.questionText.length;

    // ÁªüËÆ°Á≠îÊ°àÂíåËß£È¢òÊÄùË∑Ø
    if (question.answer && question.answer.trim()) {
      validation.statistics.withAnswers++;
    }

    if (question.solutionSteps && question.solutionSteps.trim()) {
      validation.statistics.withSolutions++;
    }

    // Ê£ÄÊü•ÈóÆÈ¢ò
    if (question.questionText.length < 10) {
      validation.issues.push(`È¢òÁõÆ ${index + 1} ÂÜÖÂÆπËøáÁü≠`);
    }

    if (!question.answer || !question.answer.trim()) {
      validation.issues.push(`È¢òÁõÆ ${index + 1} Áº∫Â∞ëÁ≠îÊ°à`);
    }

    if (question.tags.length === 0) {
      validation.issues.push(`È¢òÁõÆ ${index + 1} Áº∫Â∞ëÊ†áÁ≠æ`);
    }
  });

  validation.statistics.averageLength = questions.length > 0 
    ? Math.round(totalLength / questions.length) 
    : 0;

  validation.isValid = validation.issues.length === 0;

  return validation;
};

/**
 * ÁîüÊàêËß£ÊûêÊä•Âëä
 */
export const generateParseReport = (questions, originalText) => {
  const validation = validateParseResult(questions);

  const report = {
    timestamp: new Date().toISOString(),
    input: {
      originalLength: originalText.length,
      lineCount: originalText.split('\n').length
    },
    output: {
      questionCount: questions.length,
      successRate: questions.length > 0 ? '100%' : '0%'
    },
    validation,
    recommendations: []
  };

  // ÁîüÊàêÂª∫ËÆÆ
  if (validation.statistics.withAnswers < questions.length) {
    report.recommendations.push('Âª∫ËÆÆ‰∏∫ÊâÄÊúâÈ¢òÁõÆÊ∑ªÂä†Ê†áÂáÜÁ≠îÊ°à');
  }

  if (validation.statistics.withSolutions < questions.length * 0.8) {
    report.recommendations.push('Âª∫ËÆÆ‰∏∫Êõ¥Â§öÈ¢òÁõÆÊ∑ªÂä†Ëß£È¢òÊÄùË∑Ø');
  }

  if (validation.issues.length > 0) {
    report.recommendations.push('ËØ∑Ê£ÄÊü•Âπ∂‰øÆÂ§çÂèëÁé∞ÁöÑÈóÆÈ¢ò');
  }

  return report;
};

/**
 * ÂØºÂá∫Ëß£ÊûêÁªüËÆ°
 */
export const getParseStatistics = (questions) => {
  const stats = {
    total: questions.length,
    byType: {},
    byCategory: {},
    quality: {
      withAnswers: 0,
      withSolutions: 0,
      averageTextLength: 0,
      averageAnswerLength: 0
    }
  };

  let totalTextLength = 0;
  let totalAnswerLength = 0;

  questions.forEach(question => {
    // ÊåâÁ±ªÂûãÁªüËÆ°
    stats.byType[question.questionType] = (stats.byType[question.questionType] || 0) + 1;

    // ÊåâÂàÜÁ±ªÁªüËÆ°
    const categories = question.tags.filter(tag => 
      ['ËÆ°ÁÆó', 'ËÆ°Êï∞', 'Âá†‰Ωï', 'Êï∞ËÆ∫', 'Â∫îÁî®È¢ò', 'Ë°åÁ®ã', 'ÁªÑÂêà'].includes(tag)
    );
    categories.forEach(category => {
      stats.byCategory[category] = (stats.byCategory[category] || 0) + 1;
    });

    // Ë¥®ÈáèÁªüËÆ°
    totalTextLength += question.questionText.length;
    if (question.answer) {
      stats.quality.withAnswers++;
      totalAnswerLength += question.answer.length;
    }
    if (question.solutionSteps && question.solutionSteps.trim()) {
      stats.quality.withSolutions++;
    }
  });

  stats.quality.averageTextLength = questions.length > 0 
    ? Math.round(totalTextLength / questions.length) 
    : 0;

  stats.quality.averageAnswerLength = stats.quality.withAnswers > 0 
    ? Math.round(totalAnswerLength / stats.quality.withAnswers)
    : 0;

  return stats;
};

/**
 * Ê£ÄÊµãÈáçÂ§çÈ¢òÁõÆ
 */
export const detectDuplicates = (newQuestions, existingQuestions) => {
  const duplicates = [];
  const uniqueQuestions = [];

  newQuestions.forEach((newQ, index) => {
    const isExisting = existingQuestions.some(existingQ => 
      isSimilarQuestion(newQ, existingQ)
    );

    const isDuplicateInBatch = uniqueQuestions.some(uniqueQ => 
      isSimilarQuestion(newQ, uniqueQ)
    );

    if (isExisting || isDuplicateInBatch) {
      duplicates.push({
        index,
        question: newQ,
        reason: isExisting ? 'exists_in_database' : 'duplicate_in_batch'
      });
    } else {
      uniqueQuestions.push(newQ);
    }
  });

  return {
    duplicates,
    uniqueQuestions,
    duplicateCount: duplicates.length,
    uniqueCount: uniqueQuestions.length
  };
};

/**
 * Âà§Êñ≠‰∏§‰∏™È¢òÁõÆÊòØÂê¶Áõ∏‰ºº
 */
const isSimilarQuestion = (q1, q2) => {
  // ÁÆÄÂçïÁöÑÁõ∏‰ººÂ∫¶Ê£ÄÊµãÔºöÊØîËæÉÈ¢òÁõÆÂºÄÂ§¥30‰∏™Â≠óÁ¨¶
  const text1 = q1.questionText.substring(0, 30).replace(/\s+/g, '');
  const text2 = q2.questionText.substring(0, 30).replace(/\s+/g, '');

  return text1 === text2;
};

/**
 * È¢ÑÂ§ÑÁêÜMarkdownÊñáÊ°£
 */
export const preprocessMarkdown = (markdownText) => {
  if (!markdownText || typeof markdownText !== 'string') {
    return '';
  }

  return markdownText
    // Áªü‰∏ÄÊç¢Ë°åÁ¨¶
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n')

    // Ê∏ÖÁêÜÂ§ö‰ΩôÁöÑÁ©∫Ë°å
    .replace(/\n{3,}/g, '\n\n')

    // Áªü‰∏ÄÊ†áÈ¢òÊ†ºÂºè
    .replace(/^#+\s*/gm, '## ')

    // Ê∏ÖÁêÜË°åÈ¶ñÁ©∫Ê†º
    .replace(/^\s+/gm, '')

    // Ê∏ÖÁêÜË°åÂ∞æÁ©∫Ê†º
    .replace(/\s+$/gm, '')

    .trim();
};

/**
 * ÊèêÂèñÊñáÊ°£ÂÖÉ‰ø°ÊÅØ
 */
export const extractDocumentMeta = (markdownText) => {
  const meta = {
    hasQuestions: false,
    questionCount: 0,
    hasAnswers: false,
    hasSolutions: false,
    estimatedProcessingTime: 0,
    detectedPatterns: []
  };

  if (!markdownText) return meta;

  // Ê£ÄÊµãÈ¢òÁõÆÊ®°Âºè
  MARKDOWN_PATTERNS.questionPatterns.forEach((pattern, index) => {
    const matches = markdownText.match(new RegExp(pattern.source, 'gm'));
    if (matches && matches.length > 0) {
      meta.hasQuestions = true;
      meta.questionCount = Math.max(meta.questionCount, matches.length);
      meta.detectedPatterns.push({
        pattern: index,
        matches: matches.length,
        examples: matches.slice(0, 3)
      });
    }
  });

  // Ê£ÄÊµãÁ≠îÊ°à
  MARKDOWN_PATTERNS.answerPatterns.forEach(pattern => {
    const matches = markdownText.match(new RegExp(pattern.source, 'gm'));
    if (matches && matches.length > 0) {
      meta.hasAnswers = true;
    }
  });

  // Ê£ÄÊµãËß£È¢òÊÄùË∑Ø
  meta.hasSolutions = MARKDOWN_PATTERNS.solutionKeywords.some(keyword => 
    markdownText.includes(keyword)
  );

  // ‰º∞ÁÆóÂ§ÑÁêÜÊó∂Èó¥ÔºàÊØ´ÁßíÔºâ
  meta.estimatedProcessingTime = Math.max(100, markdownText.length / 100);

  return meta;
};

/**
 * ‰øÆÂ§çÂ∏∏ËßÅÁöÑÊ†ºÂºèÈóÆÈ¢ò
 */
export const fixCommonFormatIssues = (markdownText) => {
  return markdownText
    // ‰øÆÂ§çÈ¢òÁõÆÁºñÂè∑Ê†ºÂºè
    .replace(/‰æã(\d+)[\.ÔºéÔºö:]/g, '‰æã$1. ')
    .replace(/‰π†È¢ò(\d+)[\.ÔºéÔºö:]/g, '‰π†È¢ò$1. ')

    // ‰øÆÂ§çÁ≠îÊ°àÊ†ºÂºè
    .replace(/Á≠îÊ°à[Ôºö:]\s*/g, 'Á≠îÊ°à: ')
    .replace(/ÊúÄÁªàÁ≠îÊ°à[Ôºö:]\s*/g, 'ÊúÄÁªàÁ≠îÊ°à: ')

    // ‰øÆÂ§çÊ†áÈ¢òÊ†ºÂºè
    .replace(/^([‰æã‰π†]\d+[\.ÔºéÔºö:].*)/gm, '## $1')

    // ‰øÆÂ§çËß£È¢òÊÄùË∑ØÊ†ºÂºè
    .replace(/Ëß£È¢òÊÄùË∑Ø[Ôºö:]?\s*/g, '### Ëß£È¢òÊÄùË∑Ø\n')

    // ‰øÆÂ§ç‰∏≠ÊñáÊ†áÁÇπ
    .replace(/Ôºå\s*/g, 'Ôºå')
    .replace(/„ÄÇ\s*/g, '„ÄÇ')
    .replace(/Ôºõ\s*/g, 'Ôºõ')

    // Ê∏ÖÁêÜÂ§ö‰ΩôÁ©∫Ê†º
    .replace(/\s+/g, ' ')
    .replace(/\n\s+/g, '\n');
};

/**
 * Êô∫ËÉΩÂàÜÊÆµÂ§ÑÁêÜ
 */
export const intelligentSegmentation = (markdownText) => {
  const segments = [];
  const lines = markdownText.split('\n');
  let currentSegment = [];
  let segmentType = 'unknown';

  lines.forEach(line => {
    const trimmedLine = line.trim();

    if (!trimmedLine) {
      if (currentSegment.length > 0) {
        currentSegment.push('');
      }
      return;
    }

    // Ê£ÄÊµãÊñ∞ÁöÑÈ¢òÁõÆÊÆµËêΩ
    const isQuestionStart = matchQuestionPattern(trimmedLine);
    if (isQuestionStart) {
      // ‰øùÂ≠òÂΩìÂâçÊÆµËêΩ
      if (currentSegment.length > 0) {
        segments.push({
          type: segmentType,
          content: currentSegment.join('\n'),
          lineCount: currentSegment.length
        });
      }

      // ÂºÄÂßãÊñ∞ÊÆµËêΩ
      currentSegment = [trimmedLine];
      segmentType = 'question';
      return;
    }

    // Ê∑ªÂä†Âà∞ÂΩìÂâçÊÆµËêΩ
    currentSegment.push(trimmedLine);
  });

  // ‰øùÂ≠òÊúÄÂêé‰∏Ä‰∏™ÊÆµËêΩ
  if (currentSegment.length > 0) {
    segments.push({
      type: segmentType,
      content: currentSegment.join('\n'),
      lineCount: currentSegment.length
    });
  }

  return segments;
};
