// Supabase Configuration
import { createClient } from '@supabase/supabase-js'
console.log('✅ Supabase Config Loaded - URL:', 'https://qeedsnqbudbogqpcerqb.co');

// Supabase project configuration
const supabaseUrl = 'https://qeedsnqbudbogqpcerqb.co'  // 替换为你的Project URL
const supabaseAnonKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InFlZWRzbnFidWRib2dxcGNlcnFiIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTQ0NjMxOTIsImV4cCI6MjA3MDAzOTE5Mn0.-hWnQwcpvXjX1dMJcNaQC7AUp8jYC9ozhgjFzSHeAGo'  // 替换为你的anon public key

// Create Supabase client
export const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    autoRefreshToken: true,
    persistSession: true,
    detectSessionInUrl: true
  },
  realtime: {
    params: {
      eventsPerSecond: 10
    }
  }
})

// Database configuration
export const SUPABASE_CONFIG = {
  url: supabaseUrl,
  anonKey: supabaseAnonKey,

  // Table names
  tables: {
    questions: 'questions',
    attempts: 'attempts', 
    tags: 'tags',
    question_tags: 'question_tags'
  },

  // Storage buckets
  buckets: {
    images: 'question-images'
  }
}

// Helper functions for common operations
export const supabaseHelpers = {
  // Check if user is authenticated
  isAuthenticated: () => {
    return supabase.auth.getUser() !== null
  },

  // Get current user
  getCurrentUser: async () => {
    const { data: { user } } = await supabase.auth.getUser()
    return user
  },

  // Sign out user
  signOut: async () => {
    const { error } = await supabase.auth.signOut()
    return { error }
  }
}

export default supabase
