// src/components/auth/ProtectedRoute.js
import React from 'react';
import { useAuth } from '../../contexts/AuthProvider';
import Login from './Login';
import WaitingApproval from './WaitingApproval';
import LoadingSpinner from '../common/LoadingSpinner';

const ProtectedRoute = ({ children, requireAdmin = false }) => {
  const { user, userProfile, loading, isUserApproved, isAdmin } = useAuth();

  // 显示加载状态
  if (loading) {
    return <LoadingSpinner />;
  }

  // 用户未登录，显示登录界面
  if (!user) {
    return <Login />;
  }

  // 用户已登录但邮箱未验证
  if (!user.email_confirmed_at) {
    return (
      <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
        <div className="sm:mx-auto sm:w-full sm:max-w-md">
          <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
            <div className="text-center">
              <h2 className="text-2xl font-bold text-gray-900 mb-4">验证邮箱</h2>
              <p className="text-gray-600 mb-4">
                我们已向您的邮箱发送了验证链接，请点击邮件中的链接完成验证。
              </p>
              <p className="text-sm text-gray-500">
                邮箱：{user.email}
              </p>
              <button
                onClick={() => window.location.reload()}
                className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
              >
                我已验证，刷新页面
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // 用户已验证邮箱但等待管理员审批
  if (!isUserApproved()) {
    return <WaitingApproval userProfile={userProfile} />;
  }

  // 需要管理员权限但用户不是管理员
  if (requireAdmin && !isAdmin()) {
    return (
      <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
        <div className="sm:mx-auto sm:w-full sm:max-w-md">
          <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
            <div className="text-center">
              <h2 className="text-2xl font-bold text-red-600 mb-4">访问被拒绝</h2>
              <p className="text-gray-600">
                您没有权限访问此页面。请联系管理员。
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // 所有检查通过，显示受保护的内容
  return children;
};

export default ProtectedRoute;
