import React, { useState, useEffect } from 'react';
import { Edit2, Check, X, Plus } from 'lucide-react';

// ‰∏¥Êó∂ÁöÑ parseLabels ÂáΩÊï∞Ôºà‰πãÂêé‰ªé tagParser.js ÂØºÂÖ•Ôºâ
const parseLabels = (input) => {
  console.log('Ëß£ÊûêÊ†áÁ≠æËæìÂÖ•:', input);

  const tags = [];

  // Ëß£ÊûêËÄÅÂ∏à/Êú∫ÊûÑ
  if (input.includes('Â≤õ‰∏ª')) tags.push('Â≤õ‰∏ª');
  else if (input.includes('ÊôÆËÄÅÂ∏à')) tags.push('ÊôÆËÄÅÂ∏à');
  else tags.push('Â≤õ‰∏ª');

  // Ëß£ÊûêÂ≠¶Êúü
  const semesterMatch = input.match(/(Âõõ|‰∫î|ÂÖ≠)(Á´û|‰∏ä|‰∏ã|Êöë)/);
  if (semesterMatch) {
    tags.push(semesterMatch[0]);
  } else {
    tags.push('‰∫îÁ´ûÊöë');
  }

  // Ëß£ÊûêËÆ≤Ê¨°
  const lessonMatch = input.match(/Á¨¨(\d+|ÂçÅ?\d*)ËÆ≤/) || input.match(/(\d+)ËÆ≤/);
  if (lessonMatch) {
    let num = lessonMatch[1];
    if (num.includes('ÂçÅ')) {
      if (num === 'ÂçÅ') num = '10';
      else if (num.includes('ÂçÅ')) {
        const parts = num.split('ÂçÅ');
        num = (parseInt(parts[0]) || 1) * 10 + (parseInt(parts[1]) || 0);
      }
    }
    tags.push(`Á¨¨${parseInt(num) || 1}ËÆ≤`);
  } else {
    tags.push('Á¨¨1ËÆ≤');
  }

  // Ëß£ÊûêËØæÁ®ãÂêçÁß∞
  const titleMatch = input.match(/Á¨¨\d+ËÆ≤(.+?)(?:$|[Ôºå,])/) || 
                    input.match(/(ËÆ°Êï∞|ËÆ°ÁÆó|Âá†‰Ωï|Êï∞ËÆ∫|Â∫îÁî®È¢ò|Ë°åÁ®ã|ÁªÑÂêà)[^Ôºå,]*/) ||
                    input.match(/([‰∏Ä‰∫å‰∏âÂõõ‰∫îÂÖ≠‰∏ÉÂÖ´‰πùÂçÅ]+)$/);
  if (titleMatch) {
    const title = titleMatch[1].trim();
    if (title && title.length > 0) {
      tags.push(title);
    }
  }

  // Êô∫ËÉΩÂàÜÁ±ªÊé®Êñ≠
  let category = '';
  if (input.includes('ËÆ°Êï∞')) category = 'ËÆ°Êï∞';
  else if (input.includes('ÁªÑÂêà')) category = 'ÁªÑÂêà';
  else if (input.includes('Ë°åÁ®ã')) category = 'Ë°åÁ®ã';
  else if (input.includes('Âá†‰Ωï')) category = 'Âá†‰Ωï';
  else if (input.includes('Êï∞ËÆ∫')) category = 'Êï∞ËÆ∫';
  else if (input.includes('Â∫îÁî®')) category = 'Â∫îÁî®È¢ò';
  else if (input.includes('ËÆ°ÁÆó')) category = 'ËÆ°ÁÆó';

  if (category) tags.push(category);

  console.log('Ëß£ÊûêÁªìÊûúÊ†áÁ≠æ:', tags);
  return tags;
};

const LabelEditor = ({ onConfirm, onCancel, mathCategories }) => {
  const [labelInput, setLabelInput] = useState('');
  const [parsedTags, setParsedTags] = useState([]);
  const [editableTags, setEditableTags] = useState([]);

  const handleLabelInputChange = (input) => {
    setLabelInput(input);
    const parsed = parseLabels(input);
    setParsedTags(parsed);
    setEditableTags([...parsed]);
  };

  const updateEditableTag = (index, newValue) => {
    const newTags = [...editableTags];
    newTags[index] = newValue;
    setEditableTags(newTags);
  };

  const addEditableTag = () => {
    setEditableTags([...editableTags, 'Êñ∞Ê†áÁ≠æ']);
  };

  const removeEditableTag = (index) => {
    const newTags = editableTags.filter((_, i) => i !== index);
    setEditableTags(newTags);
  };

  const handleConfirm = () => {
    if (editableTags.length === 0) {
      alert('ËØ∑Ëá≥Â∞ëËÆæÁΩÆ‰∏Ä‰∏™Ê†áÁ≠æÔºÅ');
      return;
    }
    onConfirm(editableTags);
  };

  return (
    <div className="bg-purple-50 p-4 rounded-lg mb-4">
      <h4 className="font-medium text-purple-800 mb-3">üè∑Ô∏è Êô∫ËÉΩÊ†áÁ≠æËØÜÂà´</h4>

      <div className="mb-3">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          ËæìÂÖ•ËØæÁ®ã‰ø°ÊÅØÔºàÂ¶ÇÔºöÂ≤õ‰∏ª‰∫îÁ´ûÊöëÁ¨¨13ËÆ≤ËÆ°Êï∞ÁªºÂêà‰∫åÔºâ
        </label>
        <input
          type="text"
          value={labelInput}
          onChange={(e) => handleLabelInputChange(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg"
          placeholder="Â≤õ‰∏ª‰∫îÁ´ûÊöëÁ¨¨13ËÆ≤ËÆ°Êï∞ÁªºÂêà‰∫å"
        />
      </div>

      {labelInput && (
        <div className="bg-white p-3 rounded border mb-3">
          <h5 className="font-medium text-gray-700 mb-2">üìù AIËß£ÊûêÁªìÊûúÔºàÂèØÊâãÂä®‰øÆÊîπÔºâÔºö</h5>
          <div className="space-y-2">
            {editableTags.map((tag, index) => (
              <div key={index} className="flex items-center gap-2">
                <input
                  type="text"
                  value={tag}
                  onChange={(e) => updateEditableTag(index, e.target.value)}
                  className="flex-1 px-2 py-1 border border-gray-300 rounded text-sm"
                />
                <button
                  onClick={() => removeEditableTag(index)}
                  className="text-red-500 hover:text-red-700 text-sm px-2"
                >
                  <X size={16} />
                </button>
              </div>
            ))}
            <button
              onClick={addEditableTag}
              className="text-blue-500 hover:text-blue-700 text-sm px-2 py-1 border border-blue-300 rounded flex items-center gap-1"
            >
              <Plus size={14} />
              Ê∑ªÂä†Ê†áÁ≠æ
            </button>
          </div>

          <div className="mt-3 text-xs text-gray-600">
            <p><strong>Â••Êï∞‰∏ÉÂ§ßÂàÜÁ±ªÔºö</strong> {mathCategories.join('„ÄÅ')}</p>
            <p><strong>Âª∫ËÆÆÊ†áÁ≠æÈ°∫Â∫èÔºö</strong> [ËÄÅÂ∏à] [Â≠¶Êúü] [ËÆ≤Ê¨°] [ËØæÁ®ãÂêç] [ÂàÜÁ±ª]</p>
          </div>
        </div>
      )}

      <div className="flex gap-2 mt-3">
        <button
          onClick={handleConfirm}
          className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg flex items-center gap-2"
          disabled={editableTags.length === 0}
        >
          <Check size={16} />
          Á°ÆËÆ§Ê†áÁ≠æ
        </button>
        <button
          onClick={onCancel}
          className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-lg flex items-center gap-2"
        >
          <X size={16} />
          ÂèñÊ∂à
        </button>
      </div>
    </div>
  );
};

export default LabelEditor;
