// src/components/QuestionInput/ImportForm.js
// Êô∫ËÉΩÂØºÂÖ•Ë°®ÂçïÁªÑ‰ª∂

import React, { useState } from 'react';
import { Check, X, Edit2, AlertCircle, FileText } from 'lucide-react';
import LabelEditor from './LabelEditor';
import { parseMarkdownQuestions, validateParseResult, detectDuplicates } from '../../utils/markdownParser';
import databaseService from '../../services/DatabaseService';

const ImportForm = ({ onImport, existingQuestions, onClose }) => {
  const [importText, setImportText] = useState('');
  const [baseTags, setBaseTags] = useState([]);
  const [showLabelEditor, setShowLabelEditor] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [previewResults, setPreviewResults] = useState(null);

  const handleTagsConfirmed = (tags) => {
    setBaseTags(tags);
    setShowLabelEditor(false);
  };

  const handlePreview = () => {
    if (!importText.trim()) {
      alert('ËØ∑ÂÖàËæìÂÖ•markdownÂÜÖÂÆπ');
      return;
    }

    if (baseTags.length === 0) {
      alert('ËØ∑ÂÖàËÆæÁΩÆËØæÁ®ãÊ†áÁ≠æ');
      return;
    }

    setIsProcessing(true);

    try {
      // Ëß£Êûêmarkdown
      const parsed = parseMarkdownQuestions(importText, baseTags);

      // È™åËØÅËß£ÊûêÁªìÊûú
      const validation = validateParseResult(parsed);

      // Ê£ÄÊµãÈáçÂ§ç
      const duplicationCheck = detectDuplicates(parsed, existingQuestions);

      setPreviewResults({
        questions: duplicationCheck.uniqueQuestions,
        validation,
        duplicates: duplicationCheck.duplicates,
        totalParsed: parsed.length,
        uniqueCount: duplicationCheck.uniqueCount,
        duplicateCount: duplicationCheck.duplicateCount
      });
    } catch (error) {
      console.error('Ëß£ÊûêÂ§±Ë¥•:', error);
      alert('Ëß£ÊûêÂ§±Ë¥•: ' + error.message);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleImport = async () => {
    if (!previewResults || previewResults.uniqueCount === 0) {
      alert('Ê≤°ÊúâÂèØÂØºÂÖ•ÁöÑÈ¢òÁõÆ');
      return;
    }

    setIsProcessing(true);

    try {
      const { questions } = previewResults;
      const savedQuestions = [];

      // ‰øùÂ≠òÂà∞Êï∞ÊçÆÂ∫ì
      for (const question of questions) {
        const { data, error } = await databaseService.insertQuestion(question);
        if (error) throw new Error(error);
        savedQuestions.push(data);
      }

      // Êõ¥Êñ∞UI
      onImport(prev => [...prev, ...savedQuestions]);

      // ÈáçÁΩÆÁä∂ÊÄÅ
      setImportText('');
      setBaseTags([]);
      setPreviewResults(null);

      alert(`ÊàêÂäüÂØºÂÖ• ${savedQuestions.length} ÈÅìÈ¢òÁõÆÔºÅ`);
      onClose();
    } catch (error) {
      console.error('ÂØºÂÖ•Â§±Ë¥•:', error);
      alert('ÂØºÂÖ•Â§±Ë¥•: ' + error.message);
    } finally {
      setIsProcessing(false);
    }
  };

  const resetForm = () => {
    setImportText('');
    setBaseTags([]);
    setPreviewResults(null);
    setShowLabelEditor(false);
  };

  return (
    <div className="bg-blue-50 p-6 rounded-lg space-y-4">
      <h3 className="text-lg font-semibold text-blue-800 mb-4 flex items-center gap-2">
        <FileText size={20} />
        Êô∫ËÉΩÂØºÂÖ• - AIËß£ÊûêÊñáÊ°£
      </h3>

      {/* Ê†áÁ≠æÁºñËæëÂô® */}
      {showLabelEditor ? (
        <LabelEditor
          onConfirm={handleTagsConfirmed}
          onCancel={() => setShowLabelEditor(false)}
          initialTags={baseTags}
        />
      ) : (
        <div className="space-y-4">
          {/* Ê†áÁ≠æÁä∂ÊÄÅÊòæÁ§∫ */}
          {baseTags.length === 0 ? (
            <div className="bg-yellow-50 border border-yellow-200 p-4 rounded-lg">
              <div className="flex items-center gap-2 mb-2">
                <AlertCircle size={16} className="text-yellow-600" />
                <span className="font-medium text-yellow-800">ËØ∑ÂÖàËÆæÁΩÆËØæÁ®ãÊ†áÁ≠æ</span>
              </div>
