// src/components/QuestionInput/ImportForm.js
// Êô∫ËÉΩÂØºÂÖ•Ë°®ÂçïÁªÑ‰ª∂

import React, { useState } from 'react';
import { Check, X, Edit2, AlertCircle, FileText } from 'lucide-react';
import LabelEditor from './LabelEditor';
import { parseMarkdownQuestions, validateParseResult, detectDuplicates } from '../../utils/markdownParser';
import databaseService from '../../services/DatabaseService';

const ImportForm = ({ onImport, existingQuestions, onClose }) => {
  const [importText, setImportText] = useState('');
  const [baseTags, setBaseTags] = useState([]);
  const [showLabelEditor, setShowLabelEditor] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [previewResults, setPreviewResults] = useState(null);

  const handleTagsConfirmed = (tags) => {
    setBaseTags(tags);
    setShowLabelEditor(false);
  };

  const handlePreview = () => {
    if (!importText.trim()) {
      alert('ËØ∑ÂÖàËæìÂÖ•markdownÂÜÖÂÆπ');
      return;
    }

    if (baseTags.length === 0) {
      alert('ËØ∑ÂÖàËÆæÁΩÆËØæÁ®ãÊ†áÁ≠æ');
      return;
    }

    setIsProcessing(true);

    try {
      // Ëß£Êûêmarkdown
      const parsed = parseMarkdownQuestions(importText, baseTags);

      // È™åËØÅËß£ÊûêÁªìÊûú
      const validation = validateParseResult(parsed);

      // Ê£ÄÊµãÈáçÂ§ç
      const duplicationCheck = detectDuplicates(parsed, existingQuestions);

      setPreviewResults({
        questions: duplicationCheck.uniqueQuestions,
        validation,
        duplicates: duplicationCheck.duplicates,
        totalParsed: parsed.length,
        uniqueCount: duplicationCheck.uniqueCount,
        duplicateCount: duplicationCheck.duplicateCount
      });
    } catch (error) {
      console.error('Ëß£ÊûêÂ§±Ë¥•:', error);
      alert('Ëß£ÊûêÂ§±Ë¥•: ' + error.message);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleImport = async () => {
    if (!previewResults || previewResults.uniqueCount === 0) {
      alert('Ê≤°ÊúâÂèØÂØºÂÖ•ÁöÑÈ¢òÁõÆ');
      return;
    }

    setIsProcessing(true);

    try {
      const { questions } = previewResults;
      const savedQuestions = [];

      // ‰øùÂ≠òÂà∞Êï∞ÊçÆÂ∫ì
      for (const question of questions) {
        const { data, error } = await databaseService.insertQuestion(question);
        if (error) throw new Error(error);
        savedQuestions.push(data);
      }

      // Êõ¥Êñ∞UI
      onImport(prev => [...prev, ...savedQuestions]);

      // ÈáçÁΩÆÁä∂ÊÄÅ
      setImportText('');
      setBaseTags([]);
      setPreviewResults(null);

      alert(`ÊàêÂäüÂØºÂÖ• ${savedQuestions.length} ÈÅìÈ¢òÁõÆÔºÅ`);
      onClose();
    } catch (error) {
      console.error('ÂØºÂÖ•Â§±Ë¥•:', error);
      alert('ÂØºÂÖ•Â§±Ë¥•: ' + error.message);
    } finally {
      setIsProcessing(false);
    }
  };

  const resetForm = () => {
    setImportText('');
    setBaseTags([]);
    setPreviewResults(null);
    setShowLabelEditor(false);
  };

  return (
    <div className="bg-blue-50 p-6 rounded-lg space-y-4">
      <h3 className="text-lg font-semibold text-blue-800 mb-4 flex items-center gap-2">
        <FileText size={20} />
        Êô∫ËÉΩÂØºÂÖ• - AIËß£ÊûêÊñáÊ°£
      </h3>

      {/* Ê†áÁ≠æÁºñËæëÂô® */}
      {showLabelEditor ? (
        <LabelEditor
          onConfirm={handleTagsConfirmed}
          onCancel={() => setShowLabelEditor(false)}
          initialTags={baseTags}
        />
      ) : (
        <div className="space-y-4">
          {/* Ê†áÁ≠æÁä∂ÊÄÅÊòæÁ§∫ */}
          {baseTags.length === 0 ? (
            <div className="bg-yellow-50 border border-yellow-200 p-4 rounded-lg">
              <div className="flex items-center gap-2 mb-2">
                <AlertCircle size={16} className="text-yellow-600" />
                <span className="font-medium text-yellow-800">ËØ∑ÂÖàËÆæÁΩÆËØæÁ®ãÊ†áÁ≠æ</span>
              </div>
              <button
                onClick={() => setShowLabelEditor(true)}
                className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-lg flex items-center gap-2"
              >
                <Edit2 size={16} />
                ËÆæÁΩÆËØæÁ®ãÊ†áÁ≠æ
              </button>
            </div>
          ) : (
            <div className="bg-white p-4 rounded-lg border">
              <div className="flex justify-between items-center mb-2">
                <span className="font-medium text-gray-700">Â∑≤ËÆæÁΩÆÁöÑÊ†áÁ≠æÔºö</span>
                <button
                  onClick={() => setShowLabelEditor(true)}
                  className="text-purple-600 hover:text-purple-700 text-sm flex items-center gap-1"
                >
                  <Edit2 size={14} />
                  ‰øÆÊîπÊ†áÁ≠æ
                </button>
              </div>
              <div className="flex flex-wrap gap-1">
                {baseTags.map((tag, index) => (
                  <span
                    key={index}
                    className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs"
                  >
                    {tag}
                  </span>
                ))}
              </div>
            </div>
          )}

          {/* MarkdownËæìÂÖ• */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Á≤òË¥¥markdownÊñáÊ°£ÂÜÖÂÆπ
            </label>
            <textarea
              value={importText}
              onChange={(e) => setImportText(e.target.value)}
              rows={12}
              className="w-full p-3 border border-gray-300 rounded-lg font-mono text-sm"
              placeholder="Á≤òË¥¥AIËß£ÊûêÁöÑmarkdownÊñáÊ°£ÂÜÖÂÆπÔºåÊîØÊåÅ‰ª•‰∏ãÊ†ºÂºèÔºö
## ‰æã1. È¢òÁõÆÂÜÖÂÆπ
È¢òÁõÆÊèèËø∞...

### Ëß£È¢òÊÄùË∑Ø
Ëß£È¢òÊ≠•È™§...

**ÊúÄÁªàÁ≠îÊ°à: Á≠îÊ°àÂÜÖÂÆπ**

## ‰æã2. ‰∏ã‰∏ÄÈÅìÈ¢òÁõÆ..."
              disabled={isProcessing}
            />
          </div>

          {/* È¢ÑËßàÁªìÊûú */}
          {previewResults && (
            <div className="bg-white p-4 rounded-lg border">
              <h4 className="font-medium text-gray-800 mb-3">üìä Ëß£ÊûêÈ¢ÑËßàÁªìÊûú</h4>

              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-blue-600">
                    {previewResults.totalParsed}
                  </div>
                  <div className="text-sm text-gray-600">Ëß£ÊûêÊÄªÊï∞</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600">
                    {previewResults.uniqueCount}
                  </div>
                  <div className="text-sm text-gray-600">ÂèØÂØºÂÖ•</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-orange-600">
                    {previewResults.duplicateCount}
                  </div>
                  <div className="text-sm text-gray-600">ÈáçÂ§ç</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-purple-600">
                    {previewResults.validation.statistics.withAnswers}
                  </div>
                  <div className="text-sm text-gray-600">ÊúâÁ≠îÊ°à</div>
                </div>
              </div>

              {/* È¢òÁõÆÁ±ªÂûãÁªüËÆ° */}
              {Object.keys(previewResults.validation.statistics.byType).length > 0 && (
                <div className="mb-4">
                  <h5 className="font-medium text-gray-700 mb-2">È¢òÁõÆÁ±ªÂûãÂàÜÂ∏ÉÔºö</h5>
                  <div className="flex flex-wrap gap-2">
                    {Object.entries(previewResults.validation.statistics.byType).map(([type, count]) => (
                      <span key={type} className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
                        {type}: {count}ÈÅì
                      </span>
                    ))}
                  </div>
                </div>
              )}

              {/* Ë≠¶Âëä‰ø°ÊÅØ */}
              {previewResults.validation.issues.length > 0 && (
                <div className="bg-yellow-50 border border-yellow-200 p-3 rounded mb-4">
                  <h5 className="font-medium text-yellow-800 mb-2">‚ö†Ô∏è ÂèëÁé∞ÈóÆÈ¢òÔºö</h5>
                  <ul className="text-sm text-yellow-700 space-y-1">
                    {previewResults.validation.issues.map((issue, index) => (
                      <li key={index}>‚Ä¢ {issue}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}

          {/* Êìç‰ΩúÊåâÈíÆ */}
          <div className="flex gap-2">
            {!previewResults ? (
              <>
                <button
                  onClick={handlePreview}
                  className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center gap-2"
                  disabled={!importText.trim() || baseTags.length === 0 || isProcessing}
                >
                  {isProcessing ? 'Ëß£Êûê‰∏≠...' : 'È¢ÑËßàËß£ÊûêÁªìÊûú'}
                </button>
                <button
                  onClick={resetForm}
                  className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-lg"
                >
                  ÈáçÁΩÆ
                </button>
              </>
            ) : (
              <>
                <button
                  onClick={handleImport}
                  className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg flex items-center gap-2"
                  disabled={previewResults.uniqueCount === 0 || isProcessing}
                >
                  <Check size={16} />
                  {isProcessing ? 'ÂØºÂÖ•‰∏≠...' : `Á°ÆËÆ§ÂØºÂÖ• ${previewResults.uniqueCount} ÈÅìÈ¢òÁõÆ`}
                </button>
                <button
                  onClick={() => setPreviewResults(null)}
                  className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded-lg"
                >
                  ÈáçÊñ∞Ëß£Êûê
                </button>
              </>
            )}

            <button
              onClick={onClose}
              className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-lg flex items-center gap-2"
            >
              <X size={16} />
              ÂèñÊ∂à
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ImportForm;
